import React, { Component } from 'react'
import defaultBcg from '../images/room-1.jpeg';
import Hero from '../components/Hero';
import Banner from '../components/Banner';
import { Link } from 'react-router-dom';
import { RoomContext } from '../Context';
import StyledHero from '../components/StyleHero';

export default class SingleHouse extends Component {
    constructor(props){
        super(props);
        console.log(this.props);
        this.state={
            slug: this.props.match.params.id,
            defaultBcg
        };
    }
    // componentDidMount(){}

    static contextType = RoomContext;

    render() {
        const {getHouse} = this.context;
        const house = getHouse(this.state.slug);
        console.log(house);
        if(!house){
            return <div className="error">
                <h3>no such house could be found...</h3>
                <Link to="/houses" className="btn-primary">
                    back to houses
                </Link>
            </div>
        }
        const {name, description, capacity, size, price, extras, breakfast, pets, images} = house;
        return (
            <>
            <StyledHero img={images[0] || this.state.defaultBcg}>
                <Banner title={`${name} room`}>
                    <Link to="/houses" className="btn-primary">
                        Back to Houses
                    </Link>
                </Banner>
            </StyledHero>
            <section className="single-room">
                <div className="single-room-images">
                    {images.map((item, index) => {
                        return <img key={index} src={item} alt={name} />
                    })}
                </div>
            </section>
            </>
        )
    }
}
