import React, { Component } from 'react'
import items from './data';

const RoomContext = React.createContext();
// <RoomContext.Provider value={'hello'}
class RoomProvider extends Component {
    state={
        houses:[],
        sortedRooms: [],
        featuredHouses: [],
        loading: true,
        type: 'all',
        capacity: 1,
        price: 0,
        minPrice: 0,
        maxPrice: 0,
        minSize: 0,
        maxSize: 0,
        breakfase: false,
        pets: false
    };
    // getData{}

    componentDidMount(){
        //this.getData
        let houses = this.formatData(items);
        //console.log(houses);
        let featuredHouses = houses.filter(house => house.featured === true);
        let maxPrice = Math.max(...houses.map(item => item.price));
        let maxSize = Math.max(...houses.map(item => item.size));
        this.setState({
            houses, 
            featuredHouses, 
            sortedRooms: houses, 
            loading: false,
            price: maxPrice,
            maxPrice: maxPrice,
            maxSize: maxSize
        });
    }

    formatData(items){
        let tempItems = items.map(item => {
            let id = item.sys.id;
            let images = item.fields.images.map(image => image.fields.file.url);
            let house = {...item.fields, images, id}; //images : images overwrite
            return house;
        });
        return tempItems;
    }

    getHouse = (slug) => {
        let tempHouses = [...this.state.houses];
        const house = tempHouses.find(house => house.slug === slug);
        return house;
    }

    headleChange = event => {
        const type = event.target.type;
        const name = event.target.name;
        const value = event.target.value;
        console.log(`this is ${type}, ${name}, ${value}`);

    }

    filterHouses = () => {
        console.log("hello");
    }

    render() {
        return (
            <RoomContext.Provider value={{
                ...this.state, 
                getHouse: this.getHouse, 
                handleChange: this.handleChange
                }}>
                {this.props.children}
            </RoomContext.Provider>
        )
    }
}

const RoomConsumer = RoomContext.Consumer;

export function withRoomConsumer(Component){
    return function ConsumerWrapper(props){
        return (
        <RoomConsumer>
            {value => <Component {...props} context={value}/> }
        </RoomConsumer>
        );
    };
}

export{RoomProvider, RoomConsumer, RoomContext}
